mapper.update:
UPDATE pro_tb
SET
	pro_stock = #{pro_stock}
WHERE
	pro_id=#{pro_id}

UPDATE pro_tb
SET
	pro_stock = pro_stock-#{bas_num}
WHERE
	pro_id=#{pro_id}

UPDATE pro_tb
SET
	pro_stock = pro_stock+#{bas_num}
WHERE
	pro_id=#{pro_id}

mapper.insert:
INSERT INTO bas_tb
(
	acc_id
	, pro_id
	, bas_num
)
VALUES
(
	#{acc_id}
	, #{pro_id}
	, #{bas_num}
)

mapper.update:
UPDATE bas_tb
SET
	bas_num = #{bas_num}
WHERE
	acc_id = #{acc_id} AND pro_id=#{pro_id}

mapper.delete:
DELETE bas_tb
WHERE
	acc_id = #{acc_id} AND pro_id=#{pro_id}
